import { createSlice } from '@reduxjs/toolkit';

const cartSlice = createSlice({
  name: 'cart',
  initialState: {
    items: [],
    totalQuantity: 0,
    totalPrice: 0,
  },
  reducers: {
    addItemToCart(state, action) {
      const newItem = action.playload;
      const exixtingItem = state.items.find((item) => newItem.id);
      state.totalQuantity++;
      if (!exixtingItem) {
        state.items.push({
          itemId: newItem.id,
          price: newItem.price,
          quantity: 1,
          totalPrice: newItem.price,
          name: newItem.tittle,
        });
      } else {
        exixtingItem.quantity++;
        exixtingItem.totalPrice = exixtingItem.totalPrice + newItem.price;
      }
    },
    removeItemFromCart(state, action) {
      const id = action.playload;
      const exixtingItem = state.items.find((item) => item.id === id);
      state.totalQuantity--;
      if (exixtingItem.quantity === 1) {
        state.items = state.filter((item) => item.id !== id);
      } else {
        exixtingItem.quantity--;
        exixtingItem.totalPrice = exixtingItem.totalPrice - exixtingItem.price;
      }
    },
  },
});

export const cartActions = cartSlice.actions;

export default cartSlice;
